import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import { Container, Row, Col, Image } from "react-bootstrap";
import Navbar from "./components/Navbar";
import Header from "./components/Header";

const images = [
  "/images/aladdin.jpeg",
  "/images/alice.jpeg",
  "/images/donald.png",
  "/images/dory.jpeg",
  "/images/dumbo.jpeg",
  "/images/grumpy.jpeg",
  "/images/anna.jpeg",
  "/images/mickey.jpeg",
  "/images/minnie.jpeg",
  "/images/peter.jpeg",
  "/images/stitch.jpeg",
  "/images/winnie-the-pooh.jpeg",
  "/images/marie.jpeg",
  "/images/chip.jpeg",
  "/images/jessie.jpeg",
  "/images/jasmine.jpeg"
];

class App extends Component {
  createTable = () => {
    let containerChildren = [];
    let rowChildren = [];
    for (let i = 0; i < images.length; i++) {

      rowChildren.push(<Col>
        <Image width="200px" src={images[i]} thumbnail />
      </Col>);
      if (i % 4 === 3 || images.length -1 === i) {
        containerChildren.push(<Row>{rowChildren}</Row>);
        rowChildren = [];
      }
    }
    return containerChildren;
  }

  render() {
    return (
      <div className="App">
        <Header/>
        <Navbar/>
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Clicky Game! Click on an image to earn points, but don't click on
            any more than once!
          </p>
        </header>
        <Container>
          {this.createTable()}
        </Container>
      </div>
    );
  }
}

export default App;
