import React, { Component } from "react";
import "./App.css";
import { Container, Row, Col, Image } from "react-bootstrap";

const images = [
  "/images/aladdin.jpeg",
  "/images/alice.jpeg",
  "/images/donald.png",
  "/images/dory.jpeg",
  "/images/dumbo.jpeg",
  "/images/grumpy.jpeg",
  "/images/anna.jpeg",
  "/images/mickey.jpeg",
  "/images/minnie.jpeg",
  "/images/peter.jpeg",
  "/images/stitch.jpeg",
  "/images/winnie-the-pooh.jpeg",
  "/images/marie.jpeg",
  "/images/chip.jpeg",
  "/images/jessie.jpeg",
  "/images/jasmine.jpeg"
];

class App extends Component {
  createTable = () => {
    let containerChildren = [];
    let rowChildren = [];
    for (let i = 0; i < images.length; i++) {
      rowChildren.push(
        <Col>
          <Image width="200px" src={images[i]} thumbnail />
        </Col>
      );
      if (i % 4 === 3 || images.length - 1 === i) {
        containerChildren.push(<Row>{rowChildren}</Row>);
        rowChildren = [];
      }
    }
    return containerChildren;
  };

  render() {
    return (
      <div className="App">
        <div>
          <ul>
            <li class="brand">
              <a href="/">Clicky Game</a>
            </li>
            <li class="">Click an image to begin!</li>
            <li>Score: 0 | Top Score: 0</li>
          </ul>
        </div>
        <div className="App-title">
          <header class="header">
            <h1>Clicky Game!</h1>
            <h2>
              Click on an image to earn points, but don't click on any more than
              once!
            </h2>
          </header>
          </div>
        <Container>{this.createTable()}</Container>
        <footer class="footer">
          <div class="bottom">Clicky Game!
            <img alt="react" src="assets/images/react.svg"/>
          </div>
          </footer>
      </div>
    );
  }
}

export default App;
