import React, { Component } from "react";
import "./App.css";
import { Container, Row, Col, Image } from "react-bootstrap";

const images = [
  "/images/aladdin.jpeg",
  "/images/alice.jpeg",
  "/images/donald.png",
  "/images/dory.jpeg",
  "/images/dumbo.jpeg",
  "/images/grumpy.jpeg",
  "/images/anna.jpeg",
  "/images/mickey.jpeg",
  "/images/minnie.jpeg",
  "/images/peter.jpeg",
  "/images/stitch.jpeg",
  "/images/winnie-the-pooh.jpeg",
  "/images/marie.jpeg",
  "/images/chip.jpeg",
  "/images/jessie.jpeg",
  "/images/jasmine.jpeg"
];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      score: 0,
      topScore: 0,
      clicked: []
    };

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    const { target: { src } } = e;
    const state = this.state;
    if (state.clicked.indexOf(src) >= 0) {
      state.clicked = [];
      state.score = 0;
    } else {
      state.clicked.push(src);
      state.score += 1;
      if (state.score > state.topScore) {
        state.topScore = state.score;
      }
    }
    this.setState(state);
  }

  shuffleImages(array) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

  createTable = () => {
    let containerChildren = [];
    let rowChildren = [];
    shuffleImages(images);
    for (let i = 0; i < images.length; i++) {
      rowChildren.push(
        <Col key={i}>
          <Image onClick={this.handleClick} width="200px" src={images[i]} thumbnail />
        </Col>
      );
      if (i % 4 === 3 || images.length - 1 === i) {
        containerChildren.push(<Row key={i}>{rowChildren}</Row>);
        rowChildren = [];
      }
    }
    return containerChildren;
  };

  render() {
    return (
      <div className="App">
        <div className="NavBar">
          <ul>
            <li className="brand">
              <a href="/">Clicky Game</a>
            </li>
            <li>Click an image to begin!</li>
            <li>Score: {this.state.score} | Top Score: {this.state.topScore}</li>
          </ul>
        </div>
        <div className="App-title">
          <header className="header">
            <h1>Clicky Game!</h1>
            <h2>
              Click on an image to earn points, but don't click on any more than
              once!
            </h2>
          </header>
          </div>
        <Container>{this.createTable()}</Container>
        <footer className="footer">
          <div className="bottom">Clicky Game!
          </div>
          </footer>
      </div>
    );
  }
}

export default App;
